import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertUserSchema, 
  insertScenarioSchema, 
  insertRecommendationSchema, 
  insertUsageSchema,
  insertTwinSchema 
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // User routes
  app.post("/api/users", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);
      const user = await storage.createUser(userData);
      res.json({ success: true, user: { ...user, password: undefined } });
    } catch (error) {
      console.error("Error creating user:", error);
      res.status(400).json({ success: false, error: "Invalid user data" });
    }
  });

  app.get("/api/users/:id", async (req, res) => {
    try {
      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ success: false, error: "User not found" });
      }
      res.json({ success: true, user: { ...user, password: undefined } });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  // Financial Scenario routes
  app.get("/api/scenarios/:userId", async (req, res) => {
    try {
      const scenarios = await storage.getScenarios(req.params.userId);
      res.json({ success: true, scenarios });
    } catch (error) {
      console.error("Error fetching scenarios:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  app.post("/api/scenarios", async (req, res) => {
    try {
      const scenarioData = insertScenarioSchema.parse(req.body);
      const scenario = await storage.createScenario(scenarioData);

      // Track usage for scenario creation
      await storage.createUsageRecord({
        userId: scenarioData.userId,
        action: "scenario_save",
        creditsUsed: 1,
        metadata: { scenarioName: scenarioData.name }
      });

      // Update user credits
      await storage.updateUserCredits(scenarioData.userId, 1);

      res.json({ success: true, scenario });
    } catch (error) {
      console.error("Error creating scenario:", error);
      res.status(400).json({ success: false, error: "Invalid scenario data" });
    }
  });

  app.put("/api/scenarios/:id", async (req, res) => {
    try {
      const updateData = insertScenarioSchema.partial().parse(req.body);
      const scenario = await storage.updateScenario(req.params.id, updateData);

      if (!scenario) {
        return res.status(404).json({ success: false, error: "Scenario not found" });
      }

      res.json({ success: true, scenario });
    } catch (error) {
      console.error("Error updating scenario:", error);
      res.status(400).json({ success: false, error: "Invalid scenario data" });
    }
  });

  app.delete("/api/scenarios/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteScenario(req.params.id);
      if (!deleted) {
        return res.status(404).json({ success: false, error: "Scenario not found" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting scenario:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  // AI Recommendations routes
  app.get("/api/recommendations/:userId", async (req, res) => {
    try {
      const recommendations = await storage.getRecommendations(req.params.userId);
      res.json({ success: true, recommendations });
    } catch (error) {
      console.error("Error fetching recommendations:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  app.post("/api/recommendations", async (req, res) => {
    try {
      const recommendationData = insertRecommendationSchema.parse(req.body);
      const recommendation = await storage.createRecommendation(recommendationData);
      res.json({ success: true, recommendation });
    } catch (error) {
      console.error("Error creating recommendation:", error);
      res.status(400).json({ success: false, error: "Invalid recommendation data" });
    }
  });

  app.patch("/api/recommendations/:id/apply", async (req, res) => {
    try {
      const applied = await storage.markRecommendationApplied(req.params.id);
      if (!applied) {
        return res.status(404).json({ success: false, error: "Recommendation not found" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Error applying recommendation:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  // Usage Tracking routes
  app.get("/api/usage/:userId", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
      const usage = await storage.getUserUsage(req.params.userId, limit);
      res.json({ success: true, usage });
    } catch (error) {
      console.error("Error fetching usage:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  app.post("/api/usage", async (req, res) => {
    try {
      const usageData = insertUsageSchema.parse(req.body);
      const usage = await storage.createUsageRecord(usageData);

      // Update user credits
      await storage.updateUserCredits(usageData.userId, usageData.creditsUsed);

      res.json({ success: true, usage });
    } catch (error) {
      console.error("Error creating usage record:", error);
      res.status(400).json({ success: false, error: "Invalid usage data" });
    }
  });

  // Financial Twin routes
  app.get("/api/financial-twins", async (req, res) => {
    try {
      const industry = req.query.industry as string;
      const twins = industry 
        ? await storage.getFinancialTwinsByIndustry(industry)
        : await storage.getFinancialTwins();
      res.json({ success: true, twins });
    } catch (error) {
      console.error("Error fetching financial twins:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  app.post("/api/financial-twins", async (req, res) => {
    try {
      const twinData = insertTwinSchema.parse(req.body);
      const twin = await storage.createFinancialTwin(twinData);
      res.json({ success: true, twin });
    } catch (error) {
      console.error("Error creating financial twin:", error);
      res.status(400).json({ success: false, error: "Invalid twin data" });
    }
  });

  // AI-powered recommendation generation endpoint (placeholder for OpenAI integration)
  app.post("/api/generate-recommendations", async (req, res) => {
    try {
      const { userId, scenarioData } = req.body;

      if (!userId || !scenarioData) {
        return res.status(400).json({ success: false, error: "Missing required data" });
      }

      // For now, return mock recommendations - will be replaced with OpenAI integration
      const mockRecommendations = [
        {
          userId,
          scenarioId: scenarioData.id || null,
          title: "Optimize Marketing Spend",
          description: "Based on your current burn rate, consider reducing marketing expenses by 15% to extend runway by 2.3 months.",
          impact: "medium",
          category: "cost"
        },
        {
          userId,
          scenarioId: scenarioData.id || null,
          title: "Revenue Diversification",
          description: "Explore additional revenue streams to reduce dependency on your primary product. Consider upselling existing customers.",
          impact: "high",
          category: "revenue"
        }
      ];

      const recommendations = [];
      for (const recData of mockRecommendations) {
        const recommendation = await storage.createRecommendation(recData);
        recommendations.push(recommendation);
      }

      // Track usage for AI recommendation generation
      await storage.createUsageRecord({
        userId,
        action: "ai_recommendation",
        creditsUsed: 5,
        metadata: { scenarioId: scenarioData.id }
      });

      // Update user credits
      await storage.updateUserCredits(userId, 5);

      res.json({ success: true, recommendations });
    } catch (error) {
      console.error("Error generating recommendations:", error);
      res.status(500).json({ success: false, error: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser,
  type FinancialScenario,
  type InsertScenario,
  type AIRecommendation,
  type InsertRecommendation,
  type UsageTracking,
  type InsertUsage,
  type FinancialTwin,
  type InsertTwin
} from "@shared/schema";
import { randomUUID } from "crypto";

// Storage interface for all CFO Helper operations
export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserCredits(userId: string, creditsUsed: number): Promise<User | undefined>;

  // Financial Scenario operations
  getScenarios(userId: string): Promise<FinancialScenario[]>;
  getScenario(id: string): Promise<FinancialScenario | undefined>;
  createScenario(scenario: InsertScenario): Promise<FinancialScenario>;
  updateScenario(id: string, scenario: Partial<InsertScenario>): Promise<FinancialScenario | undefined>;
  deleteScenario(id: string): Promise<boolean>;

  // AI Recommendations operations
  getRecommendations(userId: string): Promise<AIRecommendation[]>;
  createRecommendation(recommendation: InsertRecommendation): Promise<AIRecommendation>;
  markRecommendationApplied(id: string): Promise<boolean>;

  // Usage Tracking operations
  createUsageRecord(usage: InsertUsage): Promise<UsageTracking>;
  getUserUsage(userId: string, limit?: number): Promise<UsageTracking[]>;

  // Financial Twin operations
  getFinancialTwins(): Promise<FinancialTwin[]>;
  getFinancialTwinsByIndustry(industry: string): Promise<FinancialTwin[]>;
  createFinancialTwin(twin: InsertTwin): Promise<FinancialTwin>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private scenarios: Map<string, FinancialScenario>;
  private recommendations: Map<string, AIRecommendation>;
  private usage: Map<string, UsageTracking>;
  private twins: Map<string, FinancialTwin>;

  constructor() {
    this.users = new Map();
    this.scenarios = new Map();
    this.recommendations = new Map();
    this.usage = new Map();
    this.twins = new Map();

    // Seed some financial twin data
    this.seedFinancialTwins();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id,
      companyName: insertUser.companyName || null,
      industry: insertUser.industry || null,
      teamSize: insertUser.teamSize || null,
      creditsUsed: 0,
      creditsRemaining: 100,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async updateUserCredits(userId: string, creditsUsed: number): Promise<User | undefined> {
    const user = this.users.get(userId);
    if (!user) return undefined;

    const updatedUser = {
      ...user,
      creditsUsed: (user.creditsUsed || 0) + creditsUsed,
      creditsRemaining: (user.creditsRemaining || 0) - creditsUsed
    };
    this.users.set(userId, updatedUser);
    return updatedUser;
  }

  // Financial Scenario operations
  async getScenarios(userId: string): Promise<FinancialScenario[]> {
    return Array.from(this.scenarios.values()).filter(s => s.userId === userId);
  }

  async getScenario(id: string): Promise<FinancialScenario | undefined> {
    return this.scenarios.get(id);
  }

  async createScenario(insertScenario: InsertScenario): Promise<FinancialScenario> {
    const id = randomUUID();
    const scenario: FinancialScenario = {
      ...insertScenario,
      id,
      teamSize: insertScenario.teamSize || null,
      monthlyRevenue: insertScenario.monthlyRevenue || null,
      monthlyExpenses: insertScenario.monthlyExpenses || null,
      burnRate: insertScenario.burnRate || null,
      runway: insertScenario.runway || null,
      cashBalance: insertScenario.cashBalance || null,
      scenarioData: insertScenario.scenarioData || null,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.scenarios.set(id, scenario);
    return scenario;
  }

  async updateScenario(id: string, updateData: Partial<InsertScenario>): Promise<FinancialScenario | undefined> {
    const scenario = this.scenarios.get(id);
    if (!scenario) return undefined;

    const updatedScenario = {
      ...scenario,
      ...updateData,
      updatedAt: new Date()
    };
    this.scenarios.set(id, updatedScenario);
    return updatedScenario;
  }

  async deleteScenario(id: string): Promise<boolean> {
    return this.scenarios.delete(id);
  }

  // AI Recommendations operations
  async getRecommendations(userId: string): Promise<AIRecommendation[]> {
    return Array.from(this.recommendations.values()).filter(r => r.userId === userId);
  }

  async createRecommendation(insertRecommendation: InsertRecommendation): Promise<AIRecommendation> {
    const id = randomUUID();
    const recommendation: AIRecommendation = {
      ...insertRecommendation,
      id,
      scenarioId: insertRecommendation.scenarioId || null,
      applied: false,
      createdAt: new Date()
    };
    this.recommendations.set(id, recommendation);
    return recommendation;
  }

  async markRecommendationApplied(id: string): Promise<boolean> {
    const recommendation = this.recommendations.get(id);
    if (!recommendation) return false;

    const updated = { ...recommendation, applied: true };
    this.recommendations.set(id, updated);
    return true;
  }

  // Usage Tracking operations
  async createUsageRecord(insertUsage: InsertUsage): Promise<UsageTracking> {
    const id = randomUUID();
    const usage: UsageTracking = {
      ...insertUsage,
      id,
      metadata: insertUsage.metadata || null,
      createdAt: new Date()
    };
    this.usage.set(id, usage);
    return usage;
  }

  async getUserUsage(userId: string, limit = 50): Promise<UsageTracking[]> {
    return Array.from(this.usage.values())
      .filter(u => u.userId === userId)
      .sort((a, b) => b.createdAt!.getTime() - a.createdAt!.getTime())
      .slice(0, limit);
  }

  // Financial Twin operations
  async getFinancialTwins(): Promise<FinancialTwin[]> {
    return Array.from(this.twins.values()).filter(t => t.isActive);
  }

  async getFinancialTwinsByIndustry(industry: string): Promise<FinancialTwin[]> {
    return Array.from(this.twins.values()).filter(t => t.industry === industry && t.isActive);
  }

  async createFinancialTwin(insertTwin: InsertTwin): Promise<FinancialTwin> {
    const id = randomUUID();
    const twin: FinancialTwin = {
      ...insertTwin,
      id,
      teamSize: insertTwin.teamSize || null,
      monthlyRevenue: insertTwin.monthlyRevenue || null,
      monthlyExpenses: insertTwin.monthlyExpenses || null,
      burnRate: insertTwin.burnRate || null,
      runway: insertTwin.runway || null,
      description: insertTwin.description || null,
      isActive: true,
      createdAt: new Date()
    };
    this.twins.set(id, twin);
    return twin;
  }

  private seedFinancialTwins() {
    const seedTwins = [
      {
        name: "Company Alpha",
        industry: "SaaS",
        stage: "seed",
        teamSize: 12,
        monthlyRevenue: "85000",
        monthlyExpenses: "65000",
        burnRate: "20000",
        runway: "18.5",
        description: "B2B productivity software startup with strong early traction"
      },
      {
        name: "Company Beta",
        industry: "E-commerce",
        stage: "series_a",
        teamSize: 25,
        monthlyRevenue: "150000",
        monthlyExpenses: "120000",
        burnRate: "30000",
        runway: "22.3",
        description: "Direct-to-consumer retail platform with expanding market reach"
      },
      {
        name: "Company Gamma",
        industry: "FinTech",
        stage: "seed",
        teamSize: 8,
        monthlyRevenue: "45000",
        monthlyExpenses: "55000",
        burnRate: "10000",
        runway: "14.2",
        description: "Financial services API with emerging customer base"
      },
      {
        name: "Company Delta",
        industry: "HealthTech",
        stage: "series_a",
        teamSize: 35,
        monthlyRevenue: "200000",
        monthlyExpenses: "180000",
        burnRate: "20000",
        runway: "28.7",
        description: "Healthcare automation platform with proven ROI"
      }
    ];

    seedTwins.forEach(twin => {
      const id = randomUUID();
      const financialTwin: FinancialTwin = {
        ...twin,
        id,
        teamSize: twin.teamSize || null,
        monthlyRevenue: twin.monthlyRevenue || null,
        monthlyExpenses: twin.monthlyExpenses || null,
        burnRate: twin.burnRate || null,
        runway: twin.runway || null,
        description: twin.description || null,
        isActive: true,
        createdAt: new Date()
      };
      this.twins.set(id, financialTwin);
    });
  }
}

export const storage = new MemStorage();

