  --shadow-2xs: 0px 1px 2px 0px hsl(220 15% 5% / 0.15);
  --shadow-xs: 0px 1px 3px 0px hsl(220 15% 5% / 0.25);
  --shadow-sm: 0px 1px 2px 0px hsl(220 15% 5% / 0.15), 0px 2px 4px 0px hsl(220 15% 5% / 0.20);
  --shadow: 0px 1px 3px 0px hsl(220 15% 5% / 0.30), 0px 4px 6px -2px hsl(220 15% 5% / 0.15);
  --shadow-md: 0px 4px 6px -1px hsl(220 15% 5% / 0.30), 0px 2px 4px -2px hsl(220 15% 5% / 0.15);
  --shadow-lg: 0px 10px 15px -3px hsl(220 15% 5% / 0.30), 0px 4px 6px -4px hsl(220 15% 5% / 0.15);
  --shadow-xl: 0px 20px 25px -5px hsl(220 15% 5% / 0.30), 0px 8px 10px -6px hsl(220 15% 5% / 0.15);
