import { useState } from "react";
import MetricsCard from "./MetricsCard";
import ScenarioSlider from "./ScenarioSlider";
import FinancialChart from "./FinancialChart";
import AIRecommendations from "./AIRecommendations";
import UsageTracker from "./UsageTracker";
import ScenarioTemplates from "./ScenarioTemplates";
import FinancialTwinComparator from "./FinancialTwinComparator";
import ThemeToggle from "./ThemeToggle";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  DollarSign, 
  Users, 
  TrendingUp, 
  Calendar,
  Download,
  Sparkles,
  BarChart3,
  Settings
} from "lucide-react";

export default function Dashboard() {
  //todo: remove mock functionality - replace with real state management
  const [monthlyRevenue, setMonthlyRevenue] = useState(50000);
  const [monthlyExpenses, setMonthlyExpenses] = useState(35000);
  const [teamSize, setTeamSize] = useState(8);
  const [burnRate, setBurnRate] = useState(15000);
  const [cashBalance, setCashBalance] = useState(485000);

  // Calculated metrics
  const monthlyProfit = monthlyRevenue - monthlyExpenses;
  const runway = cashBalance / burnRate;
  const costPerEmployee = monthlyExpenses / teamSize;

  //todo: remove mock functionality - replace with real chart data calculation
  const generateChartData = () => {
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const revenueData = months.map((_, i) => monthlyRevenue + (i * monthlyRevenue * 0.1));
    const expenseData = months.map((_, i) => monthlyExpenses + (i * monthlyExpenses * 0.05));
    const cashData = months.map((_, i) => Math.max(0, cashBalance - (i * burnRate)));

    return {
      forecastData: {
        labels: months,
        datasets: [
          {
            label: 'Revenue',
            data: revenueData,
            borderColor: 'hsl(var(--chart-2))',
            backgroundColor: 'hsl(var(--chart-2) / 0.1)',
            fill: true,
          },
          {
            label: 'Expenses',
            data: expenseData,
            borderColor: 'hsl(var(--chart-3))',
            backgroundColor: 'hsl(var(--chart-3) / 0.1)',
            fill: true,
          },
        ],
      },
      cashData: {
        labels: months,
        datasets: [
          {
            label: 'Cash Balance',
            data: cashData,
            borderColor: 'hsl(var(--chart-1))',
            backgroundColor: 'hsl(var(--chart-1) / 0.1)',
            fill: true,
          },
        ],
      },
    };
  };

  const { forecastData, cashData } = generateChartData();

  const handleExportReport = () => {
    console.log('Export PDF report triggered');
    // TODO: Implement PDF generation
  };

  const handleGenerateAI = () => {
    console.log('Generate AI recommendations triggered');
    // TODO: Call OpenAI API
  };

  const handleSelectTemplate = (template: any) => {
    console.log('Applying template:', template.name);
    setMonthlyRevenue(template.presets.monthlyRevenue);
    setMonthlyExpenses(template.presets.monthlyExpenses);
    setTeamSize(template.presets.teamSize);
    setBurnRate(template.presets.burnRate);
  };

  //todo: remove mock functionality - replace with real AI recommendations
  const mockRecommendations = [
    {
      id: "rec-1",
      type: "warning" as const,
      title: "Optimize Burn Rate",
      description: `Current burn rate of $${burnRate.toLocaleString()}/month could be reduced by 12% through expense optimization.`,
      impact: "high" as const,
      category: "cost" as const,
    },
    {
      id: "rec-2",
      type: "opportunity" as const,
      title: "Revenue Growth Potential",
      description: `Based on current trajectory, consider increasing revenue targets by 15% through pricing optimization.`,
      impact: "medium" as const,
      category: "revenue" as const,
    },
  ];

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b bg-card">
        <div className="flex items-center justify-between px-6 py-4">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <BarChart3 className="w-8 h-8 text-primary" />
              <h1 className="text-2xl font-bold text-foreground">CFO Helper</h1>
            </div>
            <Badge variant="outline" className="font-mono text-xs">
              Scenario Analysis
            </Badge>
          </div>
          <div className="flex items-center gap-2">
            <Button 
              variant="outline" 
              size="sm"
              onClick={handleExportReport}
              data-testid="button-export-report"
            >
              <Download className="w-4 h-4 mr-2" />
              Export Report
            </Button>
            <Button 
              size="sm"
              onClick={handleGenerateAI}
              data-testid="button-generate-ai"
            >
              <Sparkles className="w-4 h-4 mr-2" />
              AI Insights
            </Button>
            <ThemeToggle />
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <aside className="w-80 border-r bg-card p-6 min-h-screen">
          <div className="space-y-6">
            <UsageTracker
              scenariosUsed={7}
              scenariosLimit={10}
              reportsExported={3}
              reportsLimit={5}
              creditsRemaining={25}
              planType="free"
              onUpgrade={() => console.log('Upgrade triggered')}
              onPurchaseCredits={() => console.log('Purchase credits triggered')}
            />

            <Card>
              <CardHeader className="pb-4">
                <CardTitle className="text-lg font-semibold flex items-center gap-2">
                  <Settings className="w-5 h-5 text-primary" />
                  Scenario Controls
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <ScenarioSlider
                  label="Monthly Revenue"
                  value={monthlyRevenue}
                  min={0}
                  max={200000}
                  step={1000}
                  unit="$"
                  icon={<DollarSign className="w-4 h-4" />}
                  color="success"
                  description="Expected recurring monthly revenue"
                  onChange={setMonthlyRevenue}
                />
                <ScenarioSlider
                  label="Monthly Expenses"
                  value={monthlyExpenses}
                  min={0}
                  max={150000}
                  step={1000}
                  unit="$"
                  icon={<TrendingUp className="w-4 h-4" />}
                  color="warning"
                  description="Fixed and variable monthly expenses"
                  onChange={setMonthlyExpenses}
                />
                <ScenarioSlider
                  label="Team Size"
                  value={teamSize}
                  min={1}
                  max={50}
                  step={1}
                  unit=""
                  icon={<Users className="w-4 h-4" />}
                  color="primary"
                  description="Number of full-time employees"
                  onChange={setTeamSize}
                />
                <ScenarioSlider
                  label="Monthly Burn Rate"
                  value={burnRate}
                  min={5000}
                  max={100000}
                  step={1000}
                  unit="$"
                  icon={<Calendar className="w-4 h-4" />}
                  color="destructive"
                  description="Monthly cash consumption rate"
                  onChange={setBurnRate}
                />
              </CardContent>
            </Card>
          </div>
        </aside>

        {/* Main Content */}
        <main className="flex-1 p-6">
          <div className="space-y-6">
            {/* Key Metrics */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <MetricsCard
                title="Monthly Runway"
                value={Math.round(runway)}
                unit=""
                change={{
                  value: runway > 12 ? 8 : -12,
                  label: "vs last scenario",
                  type: runway > 12 ? "increase" : "decrease"
                }}
                icon={<Calendar className="w-4 h-4" />}
                variant={runway > 12 ? "success" : runway > 6 ? "warning" : "destructive"}
                description="months remaining"
              />
              <MetricsCard
                title="Monthly Profit"
                value={monthlyProfit}
                unit="$"
                change={{
                  value: monthlyProfit > 0 ? 15 : -25,
                  label: "vs expenses",
                  type: monthlyProfit > 0 ? "increase" : "decrease"
                }}
                icon={<DollarSign className="w-4 h-4" />}
                variant={monthlyProfit > 0 ? "success" : "destructive"}
                description="net income"
              />
              <MetricsCard
                title="Cost Per Employee"
                value={Math.round(costPerEmployee)}
                unit="$"
                change={{
                  value: 5,
                  label: "industry avg",
                  type: "neutral"
                }}
                icon={<Users className="w-4 h-4" />}
                variant="default"
                description="monthly cost"
              />
              <MetricsCard
                title="Cash Balance"
                value={cashBalance}
                unit="$"
                change={{
                  value: -8,
                  label: "this month",
                  type: "decrease"
                }}
                icon={<TrendingUp className="w-4 h-4" />}
                variant="warning"
                description="available funds"
              />
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <FinancialChart 
                title="Revenue vs Expenses Forecast" 
                data={forecastData}
                height={300}
              />
              <FinancialChart 
                title="Cash Runway Projection" 
                data={cashData}
                height={300}
              />
            </div>

            {/* AI Recommendations & Templates */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <AIRecommendations
                recommendations={mockRecommendations}
                loading={false}
                onGenerateRecommendations={handleGenerateAI}
                onApplyRecommendation={(id) => console.log('Apply recommendation:', id)}
              />
              <ScenarioTemplates onSelectTemplate={handleSelectTemplate} />
            </div>

            {/* Financial Twin Comparator */}
            <FinancialTwinComparator
              userMetrics={{
                teamSize,
                monthlyRevenue,
                monthlyExpenses,
                burnRate,
                runway
              }}
              onSelectTwin={(twin) => {
                console.log('Selected financial twin for analysis:', twin.name);
                // Apply twin's metrics for comparison
                setMonthlyRevenue(twin.monthlyRevenue);
                setMonthlyExpenses(twin.monthlyExpenses);
                setBurnRate(twin.burnRate);
                setTeamSize(twin.teamSize);
              }}
            />
          </div>
        </main>
      </div>
    </div>
  );
}

client/src/components/FinancialChart.tsx.content:
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from "chart.js";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface FinancialChartProps {
  title: string;
  data: {
    labels: string[];
    datasets: {
      label: string;
      data: number[];
      borderColor: string;
      backgroundColor: string;
      fill?: boolean;
    }[];
  };
  height?: number;
}

export default function FinancialChart({ title, data, height = 300 }: FinancialChartProps) {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: "top" as const,
        labels: {
          font: {
            family: "Inter",
            size: 12,
          },
          usePointStyle: true,
          pointStyle: "circle",
        },
      },
      tooltip: {
        backgroundColor: "hsl(var(--popover))",
        titleColor: "hsl(var(--popover-foreground))",
        bodyColor: "hsl(var(--popover-foreground))",
        borderColor: "hsl(var(--popover-border))",
        borderWidth: 1,
        cornerRadius: 8,
        font: {
          family: "Inter",
        },
        callbacks: {
          label: function(context: any) {
            const value = context.parsed.y;
            return `${context.dataset.label}: $${value.toLocaleString()}`;
          }
        }
      },
    },
    scales: {
      x: {
        grid: {
          color: "hsl(var(--border))",
          lineWidth: 1,
        },
        ticks: {
          color: "hsl(var(--muted-foreground))",
          font: {
            family: "Inter",
            size: 11,
          },
        },
      },
      y: {
        grid: {
          color: "hsl(var(--border))",
          lineWidth: 1,
        },
        ticks: {
          color: "hsl(var(--muted-foreground))",
          font: {
            family: "Inter",
            size: 11,
          },
          callback: function(value: any) {
            return `$${value.toLocaleString()}`;
          }
        },
      },
    },
    interaction: {
      intersect: false,
      mode: "index" as const,
    },
    elements: {
      point: {
        radius: 4,
        hoverRadius: 6,
      },
      line: {
        tension: 0.1,
      },
    },
  };

  return (
    <Card data-testid={`chart-${title.toLowerCase().replace(/\s+/g, '-')}`}>
      <CardHeader className="pb-4">
        <CardTitle className="text-lg font-semibold text-foreground">
          {title}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div style={{ height: `${height}px` }}>
          <Line data={data} options={options} />
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/FinancialTwinCompa.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Users2, 
  TrendingUp, 
  TrendingDown, 
  Eye,
  BarChart3,
  Target,
  ArrowUpRight,
  ArrowDownRight,
  Minus
} from "lucide-react";
import { cn } from "@/lib/utils";

interface BusinessProfile {
  id: string;
  name: string;
  stage: "early" | "growth" | "mature";
  industry: string;
  teamSize: number;
  monthlyRevenue: number;
  monthlyExpenses: number;
  burnRate: number;
  runway: number;
  similarity: number;
  metrics: {
    revenuePerEmployee: number;
    costPerEmployee: number;
    profitMargin: number;
    growthRate: number;
  };
}

interface FinancialTwinComparatorProps {
  userMetrics: {
    teamSize: number;
    monthlyRevenue: number;
    monthlyExpenses: number;
    burnRate: number;
    runway: number;
  };
  onSelectTwin?: (twin: BusinessProfile) => void;
}

export default function FinancialTwinComparator({ 
  userMetrics, 
  onSelectTwin 
}: FinancialTwinComparatorProps) {
  const [selectedTab, setSelectedTab] = useState("twins");

  //todo: remove mock functionality - replace with real anonymized business data from backend
  const financialTwins: BusinessProfile[] = [
    {
      id: "twin-1",
      name: "Company Alpha",
      stage: "growth",
      industry: "SaaS",
      teamSize: 10,
      monthlyRevenue: 55000,
      monthlyExpenses: 42000,
      burnRate: 18000,
      runway: 18,
      similarity: 94,
      metrics: {
        revenuePerEmployee: 5500,
        costPerEmployee: 4200,
        profitMargin: 23.6,
        growthRate: 15,
      },
    },
    {
      id: "twin-2",
      name: "Company Beta",
      stage: "growth",
      industry: "SaaS",
      teamSize: 8,
      monthlyRevenue: 48000,
      monthlyExpenses: 38000,
      burnRate: 16000,
      runway: 20,
      similarity: 89,
      metrics: {
        revenuePerEmployee: 6000,
        costPerEmployee: 4750,
        profitMargin: 20.8,
        growthRate: 12,
      },
    },
    {
      id: "twin-3",
      name: "Company Gamma",
      stage: "early",
      industry: "SaaS",
      teamSize: 6,
      monthlyRevenue: 45000,
      monthlyExpenses: 32000,
      burnRate: 12000,
      runway: 24,
      similarity: 86,
      metrics: {
        revenuePerEmployee: 7500,
        costPerEmployee: 5333,
        profitMargin: 28.9,
        growthRate: 20,
      },
    },
  ];

  // Calculate user metrics
  const userStats = {
    revenuePerEmployee: userMetrics.monthlyRevenue / userMetrics.teamSize,
    costPerEmployee: userMetrics.monthlyExpenses / userMetrics.teamSize,
    profitMargin: ((userMetrics.monthlyRevenue - userMetrics.monthlyExpenses) / userMetrics.monthlyRevenue) * 100,
    growthRate: 10, // Mock value - would come from historical data
  };

  const getComparisonIcon = (userValue: number, twinValue: number, higherIsBetter: boolean = true) => {
    const isUserBetter = higherIsBetter ? userValue > twinValue : userValue < twinValue;
    if (Math.abs(userValue - twinValue) < 0.1) {
      return <Minus className="w-3 h-3 text-muted-foreground" />;
    }
    return isUserBetter ? 
      <ArrowUpRight className="w-3 h-3 text-chart-2" /> : 
      <ArrowDownRight className="w-3 h-3 text-chart-3" />;
  };

  const getComparisonColor = (userValue: number, twinValue: number, higherIsBetter: boolean = true) => {
    const isUserBetter = higherIsBetter ? userValue > twinValue : userValue < twinValue;
    if (Math.abs(userValue - twinValue) < 0.1) return "text-muted-foreground";
    return isUserBetter ? "text-chart-2" : "text-chart-3";
  };

  const formatCurrency = (value: number) => {
    return `$${value.toLocaleString()}`;
  };

  const formatPercentage = (value: number) => {
    return `${value.toFixed(1)}%`;
  };

  return (
    <Card data-testid="financial-twin-comparator">
      <CardHeader className="pb-4">
        <CardTitle className="text-lg font-semibold flex items-center gap-2">
          <Users2 className="w-5 h-5 text-chart-1" />
          Financial Twin Comparator
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          Compare your metrics with anonymized profiles of similar businesses
        </p>
      </CardHeader>
      <CardContent>
        <Tabs value={selectedTab} onValueChange={setSelectedTab}>
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="twins" data-testid="tab-twins">Similar Companies</TabsTrigger>
            <TabsTrigger value="benchmarks" data-testid="tab-benchmarks">Benchmarks</TabsTrigger>
          </TabsList>

          <TabsContent value="twins" className="mt-4">
            <div className="space-y-4">
              {financialTwins.map((twin) => (
                <Card 
                  key={twin.id}
                  className="hover-elevate cursor-pointer transition-all"
                  onClick={() => onSelectTwin?.(twin)}
                  data-testid={`twin-${twin.id}`}
                >
                  <CardHeader className="pb-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-chart-1 to-chart-2 flex items-center justify-center">
                          <BarChart3 className="w-5 h-5 text-white" />
                        </div>
                        <div>
                          <h4 className="font-medium text-sm text-foreground">
                            {twin.name}
                          </h4>
                          <p className="text-xs text-muted-foreground">
                            {twin.industry} â€¢ {twin.stage} stage â€¢ {twin.teamSize} employees
                          </p>
                        </div>
                      </div>
                      <div className="text-right">
                        <Badge variant="outline" className="font-mono text-xs">
                          {twin.similarity}% match
                        </Badge>
                        <Progress 
                          value={twin.similarity} 
                          className="w-16 h-1 mt-1"
                        />
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <div className="grid grid-cols-2 gap-4 text-xs">
                      <div className="space-y-2">
                        <div className="flex items-center justify-between">
                          <span className="text-muted-foreground">Revenue/Employee:</span>
                          <div className="flex items-center gap-1">
                            {getComparisonIcon(userStats.revenuePerEmployee, twin.metrics.revenuePerEmployee)}
                            <span className={cn("font-mono", getComparisonColor(userStats.revenuePerEmployee, twin.metrics.revenuePerEmployee))}>
                              {formatCurrency(twin.metrics.revenuePerEmployee)}
                            </span>
                          </div>
                        </div>
                        <div className="flex items-center justify-between">
                          <span className="text-muted-foreground">Profit Margin:</span>
                          <div className="flex items-center gap-1">
                            {getComparisonIcon(userStats.profitMargin, twin.metrics.profitMargin)}
                            <span className={cn("font-mono", getComparisonColor(userStats.profitMargin, twin.metrics.profitMargin))}>
                              {formatPercentage(twin.metrics.profitMargin)}
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="space-y-2">
                        <div className="flex items-center justify-between">
                          <span className="text-muted-foreground">Monthly Runway:</span>
                          <div className="flex items-center gap-1">
                            {getComparisonIcon(userMetrics.runway, twin.runway)}
                            <span className={cn("font-mono", getComparisonColor(userMetrics.runway, twin.runway))}>
                              {twin.runway}m
                            </span>
                          </div>
                        </div>
                        <div className="flex items-center justify-between">
                          <span className="text-muted-foreground">Growth Rate:</span>
                          <div className="flex items-center gap-1">
                            {getComparisonIcon(userStats.growthRate, twin.metrics.growthRate)}
                            <span className={cn("font-mono", getComparisonColor(userStats.growthRate, twin.metrics.growthRate))}>
                              {formatPercentage(twin.metrics.growthRate)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                    <Button 
                      variant="outline" 
                      size="sm" 
                      className="w-full mt-3"
                      data-testid={`button-view-twin-${twin.id}`}
                    >
                      <Eye className="w-4 h-4 mr-2" />
                      View Detailed Comparison
                    </Button>
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="benchmarks" className="mt-4">
            <div className="space-y-4">
              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-sm font-medium flex items-center gap-2">
                    <Target className="w-4 h-4 text-chart-1" />
                    Your Performance vs Industry Average
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-3">
                    <div className="flex items-center justify-between">
